import axios from "axios";
import { response } from "express";
import { ChangeEvent, FormEvent, useEffect, useState } from "react";
import { IData, Weather } from "./Weather/Weather";

const api = {
  url: `http://api.openweathermap.org/data/2.5/`,
  key: `40c21a2013ada1779d25b9efb54161bc`,
}

export function ContainerWeather() {
  const [data, setData] = useState<IData>();

  const [location, setLocation] = useState('Moscow');
  const [value, setValue] = useState('');

  const [error, setError] = useState(false);
  const [ isLoading, setIsLoading ] = useState(true);

  useEffect(() => {
    setIsLoading(true);
    axios.get(`${api.url}weather?q=${location}&units=metric&appid=${api.key}`)
      .finally(() => { setIsLoading(false) })
      .then((resp: IData) => {
        console.log('from request: ',resp);
        setData(resp);
      })
      .catch(function (error: any) {
        console.error('Error: ', error);
        // setIsLoading(false);
        setError(true);
      });
  }, [location])

  const handleChange = (event: ChangeEvent<HTMLInputElement>) => {
    let clearValue = (event.target.value).replace(/[^a-zа-яё]/gi, '');
    setValue(clearValue);
    setError(false);
  }

  const handleSubmit = (event: FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    if (value.length !== 0) {
      setLocation(value)
    };
  }

  return (
    <Weather
      location={location} 
      handleSubmit={handleSubmit}
      onChange={handleChange} 
      value={value} 
      data={data}
      error={error}
      isLoading={isLoading}
    />
  )
}
