import { ChangeEvent, FormEvent, useEffect, useState } from "react";
import { useWeatherData } from "../hooks/useWeatherData";
import { Weather } from "./Weather/Weather";


export function ContainerWeather() {
  const { data, isLoading, error, setError } = useWeatherData();

  const [location, setLocation] = useState('Moscow');//delete this after
  const [value, setValue] = useState('');  

  const handleChange = (event: ChangeEvent<HTMLInputElement>) => {
    let clearValue = (event.target.value).replace(/[^a-zа-яё]/gi, '');
    setValue(clearValue);
  }

  const handleSubmit = (event: FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    if (value.length !== 0) {
      setLocation(value)//dispatch to location
    };
    setError(false);
  }

  return (
    <Weather
      location={location} 
      handleSubmit={handleSubmit}
      onChange={handleChange} 
      value={value} 
      data={data}
      error={error}
      isLoading={isLoading}
    />
  )
}
