import { ChangeEvent } from "react";
import './weather.css';
import { Content } from "./Content";

interface IWeather {
    onChange: (event: ChangeEvent<HTMLInputElement>) => void;
    onClick?: () => void;
    onKeyDown: (event: any) => void;
    value: string;
    data: IData;
    location: string;
}

export interface IData {
    timezone: number;
    data: object;

}

export function Weather({onChange, onKeyDown, value, data, location}: IWeather) {
    // let timezone = data.timezone;
    // let tzInHours = timezone/3600;//-6
    // let hoursInMoment = new Date().getHours();//15
    // let hoursGlobal = new Date().getUTCHours();//10
    // let GMT = hoursInMoment - hoursGlobal;//5
    // let timeInThisLocation = hoursInMoment - (GMT - tzInHours);
    // console.log(timeInThisLocation);

    return (
        <div className='weather__wrapper'>
            <input placeholder={location} className="weather__input" type="search" defaultValue={value} onKeyDown={onKeyDown} onChange={onChange} />
            {/* <button onClick={onClick} /> */}
            {data && <Content data={data.data} />}
        </div>
    )
}
