import React, { ChangeEvent, useEffect, useState } from 'react';
import './App.css';
import { fetchWeatherData } from './hooks/fetchWeatherData';

function App() {
  const [location, setLocation] = useState('Tyumen');
  const [value, setValue] = useState('');

  const handleChange = (event: ChangeEvent<HTMLInputElement>) => {
    setValue(event.target.value);
  }

  useEffect(() => {
    setLocation(value);
  }, [value])

  const handleKeyPress = (event: any) => {
    if (event.key === 'Enter') {
      setLocation(value);
    }
  }

  const weatherData = fetchWeatherData(location);

  return (
      <div className="App">
        <input type="search" value={value} onKeyPress={handleKeyPress} onChange={handleChange} />
        <button onClick={() => {setLocation(value); fetchWeatherData(location)}}/>
      </div>
  );
}

export default App;
