import { Action, ActionCreator, Reducer } from "redux";

const SEARCH_WEATHER = 'SEARCH_WEATHER';

export type RootState = {
    initialState: string;
    data: []
}

const initialState: RootState = {
    initialState: '',
    data: [],
}


type SearchWeatherAction = {
    type: typeof SEARCH_WEATHER;
    data: [];
}

export const searchWeather: ActionCreator<SearchWeatherAction> = (data: []) => ({
    type: SEARCH_WEATHER,
    data,
})


type MyAction = SearchWeatherAction;

export const rootReducer:  Reducer<RootState, MyAction> = (state = initialState, action) => {
    switch (action.type) {
        case SEARCH_WEATHER: 
            return {
                ...state,
                data: action.data,
            };  
        default:
            return state;
    }
}
