import { ChangeEvent } from "react";
import './weather.css';
import { Content } from "./Content";
import { getLocalTime } from "../utils/getLocalTime";

export interface IWeather {
    onChange: (event: ChangeEvent<HTMLInputElement>) => void;
    onClick?: () => void;
    onKeyDown: (event: any) => void;
    value: string;
    data?: IData;
    location: string;
}

export interface IData {
    data: {
        main: {
            temp: number,
            feels_like: number,
            humidity: number,
            pressure: number,
        },
        wind: {
            speed: number,
        },
        visibility: number,
        name: string,
        sys: {
            country: string
        },
        weather: [],
        timezone: number;
    }
}

export function Weather({onChange, onKeyDown, value, data, location}: IWeather) {
    let a = data ? getLocalTime(data.data.timezone) : null;
    console.log(a)
    // if (hours >= 7 || hours < 19) { day } else { night}

    return (
        <div className={
            data &&
                (getLocalTime(data.data.timezone) >= 7) && (getLocalTime(data.data.timezone) < 19)
                ? 'day weather__wrapper'
                : 'night weather__wrapper'
        }>
            <input
                placeholder={location}
                className="weather__input"
                type="search"
                defaultValue={value}
                onKeyDown={onKeyDown}
                onChange={onChange}
            />
            {data && <Content data={data.data} />}
        </div>
    )
}
