import axios from "axios";
import { ChangeEvent, KeyboardEventHandler, MouseEventHandler, useEffect, useRef, useState } from "react";
import { IData, IWeather, Weather } from "./Weather/Weather";

const api = {
  url: `http://api.openweathermap.org/data/2.5/`,
  key: `40c21a2013ada1779d25b9efb54161bc`,
}

export function ContainerWeather() {
  const [data, setData] = useState<IData>();
  const [location, setLocation] = useState('Moscow');
  const [value, setValue] = useState('');
  const [error, setError] = useState(false);

  const ref = useRef('Moscow');

  useEffect(() => {
    axios.get(`${api.url}weather?q=${ref.current}&units=metric&appid=${api.key}`)
      .then((resp: IData) => {
        console.log('from request: ',resp);
        setData(resp);
      })
      .catch(function (error: any) {
        console.error('Error: ', error);
        setError(true);
      });
  }, [ref.current])

  const handleChange = (event: ChangeEvent<HTMLInputElement>) => {
    setValue(event.target.value);
  }

  const handleKeyUp = (event: React.KeyboardEvent<HTMLInputElement>) => {
    // if (event.code === 'Enter' && (value.length !== 0)) {
      // setLocation(value.trim())
      // setError(false);
    // };
    ref.current = value.trim();
    setLocation(ref.current);
    // console.log(ref.current)
    setError(false);
  }

  return (
    <Weather
      ref={ref}
      // location={location} 
      onKeyUp={handleKeyUp} 
      onChange={handleChange} 
      value={value} 
      data={data}
      error={error}
    />
  )
}
