interface IContent {
    data: any;
}

const formatDate = (element: Date) => {
    let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    let days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

    let day = days[element.getDay()];
    let date = element.getDate();
    let month = months[element.getMonth()];
    let year = element.getFullYear();

    return `${day} ${month} ${date}, ${year}`
  }

export function Content({data}: IContent) {
    // console.log('content: ', data)
    return (
        <div className="weather__content">
            <h2 className="weather__location">{data.name}</h2>
            <p className="weather__date">{formatDate(new Date())}</p>
            <p className="weather__wind">Wind speed: {data.wind.speed}</p>
            <p className="weather__temp">Temperature: {data.main.temp}</p>
            <p className="weather__feels">Feels like: {data.main.feels_like}</p>
            <p className="weather__humidity">Humidity: {data.main.humidity}</p>
        </div>
    )
}
