import { ChangeEvent, useEffect, useRef } from "react";
import './weather.css';
import { Content } from "../Content/Content";
import { getLocalTime } from "../../utils/getLocalTime";

export interface IWeather {
    onChange: (event: ChangeEvent<HTMLInputElement>) => void;
    onClick?: () => void;
    onKeyUp: (event: React.KeyboardEvent<HTMLInputElement>) => void;
    value: string;
    data?: IData;
    location: string;
    error: boolean;
    // ref?: any;
}

export interface IData {
    data: {
        main: {
            temp: number,
            feels_like: number,
            humidity: number,
            pressure: number,
        },
        wind: {
            speed: number,
        },
        visibility: number,
        name: string,
        sys: {
            country: string
        },
        weather: [],
        timezone: number;
    }
}

export function Weather({onChange, onKeyUp, value, data, location, error}: IWeather) {
    const ref = useRef(null);
    ref.current = null;

    useEffect(() => {
        console.log('REF: ', ref.current)
    }, [ref.current])

    return (
        <div className={
            data &&
                (getLocalTime(data.data.timezone) >= 7) && (getLocalTime(data.data.timezone) < 19)
                ? 'day weather__wrapper'
                : 'night weather__wrapper'
        }>
            <input
                type="text"
                ref={ref}
                placeholder={location}
                className="weather__input"
                defaultValue={value}
                onKeyUp={onKeyUp}
                onChange={onChange}
            />
            {error && 
                <p className="error__message">
                    Request failed. Enter correct data and try again!
                </p>
            }
            {data && <Content data={data.data} />}
        </div>

    )
}
