import { formatCountry } from '../utils/formatCountry';
import { formatDate } from '../utils/formatDate';
import './content.css';


interface IContent {
    data: any;
}

export function Content({data}: IContent) {
    // console.log('content: ', data)
    return (
        <div className="content">
            <h2 className="content__location">
                {data.name}
                <span className='content__country'>{formatCountry(data.sys.country)}</span>
            </h2>
            <div className="content__date date">
                <span className="date__date date__parag">
                    {formatDate(new Date()).month + ' ' + formatDate(new Date()).date + ', ' + formatDate(new Date()).year}
                </span>
                <span className="date__day date__parag">
                    {formatDate(new Date()).day}
                </span>
            </div>
            <div className='weather__snow'>
                {data.weather &&
                        data.weather.map((elem: any) => {
                            return elem.description
                            ? 
                            <div key={elem.id}>{elem.description}</div>
                            : null;
                        })
                }
            </div>
            <ul className='content__list'>
                <li className="content__item">
                    <span className='content__title content__temp'>Temperature:</span>
                    <span className='content__value'>{data.main.temp}</span>
                    <span>℃</span>
                </li>
                <li className="content__item">
                    <span className='content__title content__feels'>Feels like:</span>
                    <span className='content__value'>{data.main.feels_like}</span>
                    <span>℃</span>
                </li>
                <li className="content__item">
                    <span className='content__title content__wind'>Wind speed:</span>
                    <span className='content__value'>{data.wind.speed}</span>
                    <span>m/sec</span>
                </li>
                <li className="content__item">
                    <span className='content__title content__humidity'>Humidity:</span>
                    <span className='content__value'>{data.main.humidity}</span>
                    <span>%</span>
                </li>
            </ul>
        </div>
    )
}
