import axios from "axios";
import { ChangeEvent, MouseEventHandler, useEffect, useState } from "react";
import { Weather } from "./Weather";

const api = {
  url: `http://api.openweathermap.org/data/2.5/`,
  key: `40c21a2013ada1779d25b9efb54161bc`,
}

export function ContainerWeather() {
  const [data, setData] = useState();
  const [location, setLocation] = useState('Tyumen');
  const [value, setValue] = useState('');

  useEffect(() => {
    axios.get(`${api.url}weather?q=${location}&units=metric&appid=${api.key}`)
      .then((resp: any) => {
        // console.log(resp);
        setData(resp);
      })
      .catch(function (error: any) {
        console.error(error);
      });
  }, [location])

  const handleChange = (event: ChangeEvent<HTMLInputElement>) => {
    setValue(event.target.value);
  }

  const handleClick = () => {
    setLocation(value)
  }

  return (
    <Weather onChange={handleChange} onClick={handleClick} value={value} data={data !== undefined ? data : null } />
  )
}
