import './content.css';

interface IContent {
    data: any;
}

const formatDate = (element: Date) => {
    let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
    let days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];

    let day = days[element.getDay()];
    let date = element.getDate();
    let month = months[element.getMonth()];
    let year = element.getFullYear();

    return {
        day:  `${day}`,
        month: `${month}`,
        date: `${date}`,
        year: `${year}`,
    }
  }

export function Content({data}: IContent) {
    // console.log('content: ', data)
    return (
        <div className="content">
            <h2 className="content__location">{data.name}</h2>
            <div className="content__date date">
                <span className="date__day date__parag">{formatDate(new Date()).day}</span>
                <span className="date__date date__parag">
                    {formatDate(new Date()).month + ' ' + formatDate(new Date()).date + ', ' + formatDate(new Date()).year}
                </span>
                {}
            </div>
            <ul className='content__list'>
                <li className="content__item content__wind">Wind speed: {data.wind.speed}</li>
                <li className="content__item content__temp">Temperature: {data.main.temp}</li>
                <li className="content__item content__feels">Feels like: {data.main.feels_like}</li>
                <li className="content__item content__humidity">Humidity: {data.main.humidity}</li>
            </ul>
        </div>
    )
}
